import React from 'react';
import { Github, FileText } from 'lucide-react';
import './Header.css';

const Header = () => {
  const githubRepo = process.env.REACT_APP_GITHUB_REPO;
  const githubUser = process.env.REACT_APP_GITHUB_USER;

  return (
    <header className="header">
      <div className="header-content">
        <div className="header-left">
          <FileText className="header-icon" size={24} />
          <h1 className="header-title">Markdown Preview</h1>
          <span className="header-subtitle">Visor Markdown Vitaminado</span>
        </div>

        <div className="header-right">
          {githubRepo && (
            <a 
              href={githubRepo}
              target="_blank"
              rel="noopener noreferrer"
              className="github-link"
              title="Ver en GitHub"
            >
              <Github size={20} />
              <span>GitHub</span>
            </a>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
