import React, { useState, useRef } from 'react';
import { Upload, FileText, Trash2 } from 'lucide-react';
import './MarkdownEditor.css';

const MarkdownEditor = ({ markdown, onChange, onSelectionChange }) => {
  const [isDragOver, setIsDragOver] = useState(false);
  const textareaRef = useRef(null);
  const fileInputRef = useRef(null);

  const handleFileUpload = (file) => {
    if (file && file.type === 'text/markdown' || file.name.endsWith('.md')) {
      const reader = new FileReader();
      reader.onload = (e) => {
        onChange(e.target.result);
      };
      reader.readAsText(file);
    } else {
      alert('Por favor, selecciona un archivo Markdown (.md)');
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragOver(false);

    const files = Array.from(e.dataTransfer.files);
    if (files.length > 0) {
      handleFileUpload(files[0]);
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    setIsDragOver(false);
  };

  const handleFileInputChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      handleFileUpload(file);
    }
  };

  const handleTextareaChange = (e) => {
    onChange(e.target.value);
  };

  const handleSelectionChange = () => {
    if (textareaRef.current) {
      const { selectionStart, selectionEnd } = textareaRef.current;
      const selectedText = markdown.substring(selectionStart, selectionEnd);
      onSelectionChange(selectedText.trim());
    }
  };

  const clearContent = () => {
    onChange('');
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  return (
    <div className="markdown-editor">
      <div className="editor-header">
        <div className="editor-title">
          <FileText size={18} />
          <span>Editor Markdown</span>
        </div>
        <div className="editor-actions">
          <button
            onClick={() => fileInputRef.current?.click()}
            className="action-button upload-button"
            title="Subir archivo MD"
          >
            <Upload size={16} />
            <span>Subir</span>
          </button>
          {markdown && (
            <button
              onClick={clearContent}
              className="action-button clear-button"
              title="Limpiar contenido"
            >
              <Trash2 size={16} />
            </button>
          )}
        </div>
      </div>

      <div
        className={`editor-content ${isDragOver ? 'drag-over' : ''}`}
        onDrop={handleDrop}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
      >
        {!markdown ? (
          <div className="empty-state">
            <Upload size={48} className="empty-icon" />
            <h3>Sube o pega tu Markdown</h3>
            <p>Arrastra un archivo .md aquí o pega tu contenido en el área de texto</p>
            <button
              onClick={() => fileInputRef.current?.click()}
              className="upload-button-large"
            >
              <Upload size={20} />
              Seleccionar archivo
            </button>
          </div>
        ) : (
          <textarea
            ref={textareaRef}
            value={markdown}
            onChange={handleTextareaChange}
            onSelect={handleSelectionChange}
            onMouseUp={handleSelectionChange}
            onKeyUp={handleSelectionChange}
            placeholder="Escribe o pega tu Markdown aquí..."
            className="markdown-textarea"
            spellCheck="false"
          />
        )}
      </div>

      <input
        ref={fileInputRef}
        type="file"
        accept=".md,.markdown"
        onChange={handleFileInputChange}
        style={{ display: 'none' }}
      />
    </div>
  );
};

export default MarkdownEditor;
