export const MODELS = [
  {
    id: 'anthropic/claude-3.7-sonnet',
    name: 'Claude 3.7 Sonnet',
    provider: 'Anthropic',
    isFree: false,
    enabled: process.env.REACT_APP_ENABLE_CLAUDE_SONNET === 'true',
    description: 'Modelo avanzado de Anthropic para tareas complejas'
  },
  {
    id: 'openai/gpt-4.1',
    name: 'GPT-4.1',
    provider: 'OpenAI',
    isFree: false,
    enabled: process.env.REACT_APP_ENABLE_GPT_4_1 === 'true',
    description: 'Último modelo de OpenAI con capacidades mejoradas'
  },
  {
    id: 'openai/gpt-4.1-mini',
    name: 'GPT-4.1 Mini',
    provider: 'OpenAI',
    isFree: false,
    enabled: process.env.REACT_APP_ENABLE_GPT_4_1_MINI === 'true',
    description: 'Versión optimizada y rápida de GPT-4.1'
  },
  {
    id: 'deepseek/deepseek-r1-0528:free',
    name: 'DeepSeek R1',
    provider: 'DeepSeek',
    isFree: true,
    enabled: process.env.REACT_APP_ENABLE_DEEPSEEK_R1 === 'true',
    description: 'Modelo gratuito de DeepSeek para razonamiento'
  },
  {
    id: 'qwen/qwen3-30b-a3b:free',
    name: 'Qwen3 30B',
    provider: 'Qwen',
    isFree: true,
    enabled: process.env.REACT_APP_ENABLE_QWEN3_30B === 'true',
    description: 'Modelo gratuito de Qwen con 30B parámetros'
  },
  {
    id: 'meta-llama/llama-4-maverick:free',
    name: 'Llama 4 Maverick',
    provider: 'Meta',
    isFree: true,
    enabled: process.env.REACT_APP_ENABLE_LLAMA_4_MAVERICK === 'true',
    description: 'Modelo gratuito de Meta Llama 4'
  }
];

export const getEnabledModels = () => {
  return MODELS.filter(model => model.enabled);
};

export const getModelById = (id) => {
  return MODELS.find(model => model.id === id);
};
